.card {
  border: 1px solid #ddd;
  border-radius: 8px;
  padding: 20px;
  box-shadow: 0 0 5px rgba(0,0,0,0.1);
}

.btn {
  background: #008CBA;
  color: white;
  padding: 10px 20px;
  border: none;
  cursor: pointer;
}

.flex-box {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.tooltip:hover::after {
  content: attr(data-tip);
  background: black;
  color: white;
  padding: 5px;
}

@media screen and (max-width: 768px) {
  .menu {
    display: block;
  }
  .menu-item {
    font-size: 14px;
    padding: 8px;
  }
}

.loader {
  border: 6px solid #f3f3f3;
  border-top: 6px solid #3498db;
  border-radius: 50%;
  width: 40px;
  height: 40px;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

.modal {
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: white;
  padding: 30px;
  box-shadow: 0 0 10px rgba(0,0,0,0.3);
}

.dropdown-content {
  display: none;
  position: absolute;
  background-color: #f9f9f9;
  min-width: 160px;
  box-shadow: 0px 8px 16px rgba(0,0,0,0.2);
  z-index: 1;
}

.toast {
  position: fixed;
  bottom: 20px;
  right: 20px;
  background: #333;
  color: white;
  padding: 15px;
  border-radius: 5px;
}

.timeline::before {
  content: '';
  position: absolute;
  left: 50%;
  top: 0;
  bottom: 0;
  width: 2px;
  background: #ccc;
}

input[type="range"]::-webkit-slider-thumb {
  appearance: none;
  height: 20px;
  width: 20px;
  background: #4CAF50;
  cursor: pointer;
}

.glassy {
  backdrop-filter: blur(10px);
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

:root {
  --primary-color: #0d6efd;
  --font-stack: 'Segoe UI', sans-serif;
}

body {
  font-family: var(--font-stack);
  color: var(--primary-color);
}
